mapper.update:
UPDATE MEMBERSHIP
   SET PASSWORD      = #{password},
       GRADE         = #{grade},
       PROFILE_IMAGE = #{profileImage,jdbcType=VARCHAR}
 WHERE USER_ID = #{userId}

UPDATE MEMBERSHIP
   SET EMAIL_AUTH       = #{emailAuth},
       EMAIL_AUTH_TOKEN = #{emailAuthToken}
 WHERE USER_ID = #{userId}


UPDATE MEMBERSHIP
SET EMAIL_AUTH_TOKEN = #{token}
WHERE EMAIL = #{email}
