mapper.insert:
INSERT INTO L_REACTION (
    REACTION_CODE, USER_ID, REACTION_TYPE, TARGET_TYPE, TARGET_CODE
) VALUES (
    #{reactionCode}, #{userId}, #{reactionType}, #{targetType}, #{targetCode}
)

mapper.delete:
DELETE FROM L_REACTION
WHERE USER_ID = #{userId}
  AND TARGET_TYPE = #{targetType}
  AND TARGET_CODE = #{targetCode}

mapper.select:
SELECT COUNT(*) FROM L_REACTION
WHERE TARGET_TYPE = #{targetType}
  AND TARGET_CODE = #{targetCode}
  AND REACTION_TYPE = #{reactionType}
