mapper.select:
SELECT A.*, B.* FROM ( SELECT tt3.rnum AS no, tt3.food_name, tt3.st_grams, tt3.cal, tt3.carb, tt3.fat, tt3.prot, tt3.na FROM ( SELECT ROWNUM AS rnum, tt2.* FROM ( SELECT * FROM food WHERE food_name LIKE '%' || #{searchWord} || '%' ORDER BY food_name ASC ) tt2
WHERE ROWNUM <= (#{pageSize} * (#{pageNo} - 1) + #{pageSize}) ) tt3 WHERE rnum >= (#{pageSize} * (#{pageNo} - 1) + 1)
) A CROSS JOIN ( SELECT COUNT(*) AS total_cnt FROM food WHERE food_name LIKE '%' || #{searchWord} || '%' ) B
